import React, {useState} from 'react';
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Link from "@material-ui/core/Link";
import {NavLink, Link as RouterLink} from "react-router-dom";
import {useSelector, useDispatch} from "react-redux";
import  {Button} from "@material-ui/core";
import '../Header/header.css'
import {removeUserData,removeJwt} from "../../store/slices/userSlice";
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import WorkOutlineSharpIcon from '@material-ui/icons/WorkOutlineSharp';

export default function ButtonAppBar() {

    const user = useSelector(state => state.user.userData)
    const dispatch = useDispatch()

    const logout = () => {
        dispatch(removeJwt())
        dispatch(removeUserData())
    }

    return (

        <div className='root'>
            <AppBar position="static">
                <Toolbar>
                    <WorkOutlineSharpIcon/>
                    <Typography variant="h6" className='title'>
                        Find your job
                    </Typography>
                    <nav className='nav-text'>
                        <Link className="nav-text" component={NavLink} to ="/">Home</Link>
                        <Link className="nav-text" component={NavLink} to ="/register">Register</Link>
                        <Link className="nav-text" component={NavLink} to ="/createAd">Add Job Ad</Link>
                        {
                            !!user ? (
                                <>
                                    <span>{`${user.username}`}</span>
                                    <Link className="nav-text" component={Button} color="secondary" onClick={logout}><ExitToAppIcon/></Link>
                                </>
                            ) : (
                                <Link className="nav-text-login" component={NavLink} to="/login">Prisijungti</Link>
                            )
                        }
                    </nav>

                </Toolbar>
            </AppBar>
        </div>
    );
}
