import React, {Component, useEffect, useState} from 'react';
import adService from "./jobAdApi"

class Ads extends Component{
    constructor(props) {
        super(props);

        this.state = {
            ads: []
        }
    }
    componentDidMount(){
        adService.getAds().then((res) => {
            this.setState({ads : res.data})
        })};

    render() {
        return (
            <div>
                <h2 className="text-center"> Job Ads</h2>
    <div className="row">
        <table className= "table table-striped table-bordered">

            <thead>
            <tr>
                <th> Job ad Name</th>
                <th> Job ad description</th>
            </tr>
            </thead>
            <tbody>
            {
                this.state.ads.map(
                    ads =>
                        <tr key = {ads.id}>
                            <td> {ads.name}</td>
                            <td> {ads.description}</td>

                        </tr>
                )
            }
            </tbody>

        </table>
    </div>
            </div>
        )
    }
}

export default Ads
