import {Form, Formik, Field, ErrorMessage} from "formik";
import {useHistory} from "react-router-dom";
import * as Yup from "yup"
import {register} from "../../api/UserApi";
import React from "react";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";

// const validationSchema = Yup.object().shape({
//     username: Yup.string()
//         .max(15, 'Username privalo buti trumpesnis nei 15')
//         .required(),
//     name: Yup.string()
//         .max(15, 'Vardas privalo buti trumpesnis nei 15')
//         .required,
//     surname: Yup.string()
//         .max(15, 'Vardas privalo buti trumpesnis nei 15')
//         .required,
//     password: Yup.string()
//         .min(4, 'Slaptazodis negali buti trumpesnis nei 4')
//         .required()
// })

export default () => {
    const history = useHistory();
    const [age, setAge] = React.useState('');
    const [open, setOpen] = React.useState(false);

    const handleOnSubmit = (formValues, {setSubmitting}) => {
        setSubmitting(true);
        register(formValues)
            .then(() => {
                history.push("/");
            })
            .finally(() => setSubmitting(false))
    }
    const handleChange = (event) => {
        setAge(event.target.value);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleOpen = () => {
        setOpen(true);
    };
    return (
        <Formik
            initialValues={{
                id: '',
                name: '',
                surname: '',
                username: '',
                password: '',
            }}
            onSubmit={handleOnSubmit}
            // validationSchema={validationSchema}
        >
            {(props ) => (
                <Form>
                    <div>
                        <FormControl >
                            <InputLabel id="demo-controlled-open-select-label">Role</InputLabel>
                            <Select
                                labelId="demo-controlled-open-select-label"
                                id="demo-controlled-open-select"
                                open={open}
                                onClose={handleClose}
                                onOpen={handleOpen}
                                value={age}
                                onChange={handleChange}
                            >
                                <MenuItem value="">
                                    <em>None</em>
                                </MenuItem>
                                <MenuItem value={10}>Ten</MenuItem>
                                <MenuItem value={20}>Twenty</MenuItem>
                                <MenuItem value={30}>Thirty</MenuItem>
                            </Select>
                        </FormControl>
                    </div>
                    <div className="form-group">
                        <label htmlFor="id">id:</label>
                        <Field name="id" id="id" className="form-control" placeholder="Please enter name " />
                    </div>
                    <div className="form-group">
                        <label htmlFor="name">Name:</label>
                        <Field name="name" id="name" className="form-control" placeholder="Please enter name " />
                    </div>
                    <div className="form-group">
                        <label htmlFor="surname">Surname:</label>
                        <Field name="surname" id="surname" className="form-control" placeholder="Please enter surname " />
                    </div>
                    <div className="form-group">
                        <label htmlFor="username">Username:</label>
                        <Field name="username" id="username" className="form-control" placeholder="Please enter username " />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password:</label>
                        <Field name="password" id="password" className="form-control" placeholder="Please enter name password" />
                        <button type="submit" className="btn btn-primary mt-2" disabled={props.isSubmitting}>Register</button>
                    </div>
                </Form>

            )}
        </Formik>
    )
}
