import {Form, Formik, Field, ErrorMessage} from "formik"
import { useHistory } from "react-router-dom";
import {addAd} from "../api/api";


export default () => {
    const history = useHistory();

    const handleOnSubmit = (adData, { setSubmitting }) => {
        setSubmitting(true);
        addAd(adData)
            .then(() => {
                history.push("/");
            })
            .finally( () => {
                setSubmitting(false)
            })
    }

    return (
        <Formik
            initialValues={{
                name: '',
                description: '',
                wage: '',
                location: ''
            }}
            onSubmit={handleOnSubmit}>
            {(props) => (
                <>

                    <Form className="mx-4">
                        <div className="form-group">
                            <label htmlFor="name">Name:</label>
                            <Field name="name" id="name" className="form-control" placeholder="Please enter name" />
                        </div>
                        <div className="form-group">
                            <label htmlFor="description">Description:</label>
                            <Field name="description" id="description" className="form-control" placeholder="description" />

                        </div>

                        <div className="form-group">
                            <label htmlFor="wage">Wage:</label>
                            <Field name="wage" id="wage" className="form-control" placeholder="Wage" />

                        </div>

                        <div className="form-group">
                            <label htmlFor="location">Location:</label>
                            <Field name="location" id="location" className="form-control"
                                   placeholder="location" component="textarea"/>
                        </div>
                        <button type="submit" className="btn btn-primary mt-2" disabled={props.isSubmitting}>Submit</button>
                    </Form>
                </>
            )
            }
        </Formik>
    )
}
